    private void cmdBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdBuscarActionPerformed
        Lugares lugares = new Lugares();
        lugares.buscarCiudad(ciudadSelecionada(paisSelecionado()));
        lugares.busquedad(busquedaSelecionada());
        lugares.url();
        lugares.xmlGoogle();
        Tabla obj = new Tabla();
        obj.setObj(this);
        obj.setVisible(true);
        dispose();
    }//GEN-LAST:event_cmdBuscarActionPerformed

