    public String ciudadSelecionada(String ciudad) {
        String ciudadSeleccionada = "";

        int ciudadesColombia = cbxCiudad.getSelectedIndex();
        switch (ciudadesColombia) {
            case 0:
                ciudadSeleccionada = "Cucuta";
                break;
            case 1:
                ciudadSeleccionada = "Medellin";
                break;
            case 2:
                ciudadSeleccionada = "Barranquilla";
                break;
            case 3:
                ciudadSeleccionada = "Cali";
                break;

        }
        return ciudadSeleccionada;
    }

    public String paisSelecionado() {
        String paisSelecionado = "";
        int posicion = 0;
        switch (posicion) {
            case 0:
                paisSelecionado = "colombia";
                break;
        }
        return paisSelecionado;
    }

    public String busquedaSelecionada() {
        String busqueda = "";
        int posicion = cbxBusqueda.getSelectedIndex();
        switch (posicion) {
            case 0:
                busqueda = "cafe";
                break;
            case 1:
                busqueda = "park";
                break;
            case 2:
                busqueda = "parking";
                break;
            case 3:
                busqueda = "school";
                break;
            case 4:
                busqueda = "university";
                break;
            case 5:
                busqueda = "restaurant";
                break;
            case 6:
                busqueda = "";
                break;

        }
        return busqueda;
    }

    public String titulo(){
        String busqueda = this.busquedaSelecionada();
        return busqueda;
    }
    public HashMap lista() {
        Lugares lugares = new Lugares();
        return lugares.xmlGoogle();
    }

